import React from "react";
import Card from "@material-tailwind/react/Card";
import CardImage from "@material-tailwind/react/CardImage";
import CardBody from "@material-tailwind/react/CardBody";
import CardFooter from "@material-tailwind/react/CardFooter";
import H6 from "@material-tailwind/react/Heading6";
import Paragraph from "@material-tailwind/react/Paragraph";
import Button from "@material-tailwind/react/Button";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
import FavoriteIcon from "@material-ui/icons/Favorite";
import { useState } from "react";
import "./Projects.css";
import { useEffect } from "react";
import axios from "axios";
import { makeStyles } from "@material-ui/core";
import { useHistory } from "react-router-dom";

const useStyle = makeStyles({
  cards: {
    marginTop: "40px",
  },
});
export default function Projects({ match }) {
  const classes = useStyle();
  const [projects, setProjects] = useState([]);
  useEffect(() => {
    axios
      .get(`http://localhost:3031/portfolio/`)
      .then((response) => response.data)
      .then((data) => setProjects(data));
  }, []);

  const [liked, setLiked] = useState(false);
  const [like, setLike] = useState({
      like: 0
  });

  const history = useHistory();
  const handleClick = () => {

    setLiked(!liked);
    setLike(like++)
    console.log(like)
    if (match?.params?.id) {

      axios
        .patch(`http://localhost:3031/portfolio/${match?.params?.id}`)
        .then((like) => setLike(like));

    }
  };
  return (
    <div className={classes.cards}>
      {projects.map((project) => {
        return (
          <Card className="gallery" key={project.id}>
            <CardImage
              className="galery-cell"
              src={project.image}
              alt="Card Image"
            />

            <CardBody>
              <H6 color="gray">{project.title}</H6>
              <Paragraph color="gray">{project.description}</Paragraph>
            </CardBody>

            <CardFooter key={project.id}>
              <Button
                color="lightGreen"
                size="lg"
                ripple="light"
                onClick={handleClick}
              >
                {!liked ? <FavoriteBorderIcon /> : <FavoriteIcon />}
                {project.like}
              </Button>
            </CardFooter>
          </Card>
        );
      })}
    </div>
  );
}
