import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Button, Dialog, IconButton, Input } from "@material-ui/core";
import AddCircleOutlineIcon from "@material-ui/icons/AddCircleOutline";
import CloseIcon from "@material-ui/icons/Close";
// import { useState } from "react";
// import axios from "axios";
// import { useHistory } from "react-router";

const useStyles = makeStyles((theme) => ({
  buttonmaterial: {
    borderRadius: "45px",
    backgroundColor: "#628395",
    boxShadow: "none",
    color: "white",
    border: "none",
    width: "400px",
    height: "100px",
    outline: "none",
    margin: "20px",
    textTransform: "uppercase",
    "&:hover": {
      backgroundColor: "#262A53",
    },
  },
}));

export default function NewProject({ primary }) {
  const classes = useStyles();
//   const history = useHistory();
const [open, setOpen] = UseState(false);
  const [project, setProject] = useState({
    title: "",
    description:"", 
    image:"",
    like:""
  });
  const handleClickOpen = () => {
    setOpen(true);
  };
const handleClose = () => {
   setOpen(false);
 };

  const handleChange = (e) => {
    setProject({ ...project, [e.target.name]: e.target.value });
  };

//   const handleClick = async () => {
//     await axios
//       .post("http://localhost:3031/portfolio", project)
//       .then((response) => {
//         setProject(response.data);
//         setOpen(false);
//         window.location.reload();
//       });
//   };

  return (
    <div>

      <Button
        className={classes.buttonmaterial}
        variant="contained"
        onClick={handleClickOpen}
        startIcon={<AddCircleOutlineIcon />}
      >
        Add new project realized!
      </Button>

      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <IconButton onClick={handleClose}>
          <CloseIcon />
        </IconButton>
        <Input
          indication="Prénom"
          value=""
          name="firstname"

        />
        <Input
          indication="Nom"
          value=""
          name="lastname"

        />
        <Input
          indication="Email"
          value=""
          type="email"
          name="email" 
        />
        <Input
          indication="Password"
          value=""
          type="password"
          name="password"

        />
        <Button className={classes.buttonmaterial2} onClick={handleClose} >
          Valider
        </Button>
        <Button className={classes.buttonmaterial2} onClick={handleClose}>
          Annuler
        </Button>
      </Dialog>
    </div>
  );
}
