import React from "react";
import Modal from "@material-tailwind/react/Modal";
import ModalHeader from "@material-tailwind/react/ModalHeader";
import ModalBody from "@material-tailwind/react/ModalBody";
import ModalFooter from "@material-tailwind/react/ModalFooter";
import Button from "@material-tailwind/react/Button";
import Textarea from "@material-tailwind/react/Textarea";
import { makeStyles } from "@material-ui/core";
import Input from "@material-tailwind/react/Input";
import ImageIcon from "@material-ui/icons/Image";
import { useState } from "react";
import axios from "axios";
import { ShortTextTwoTone } from "@material-ui/icons";


const useStyle = makeStyles({
  input: {
    width: "200px",
  },
});

export default function NewProject() {
  const classes = useStyle();
  const [showModal, setShowModal] = React.useState(false);
  const [imageUrl, setImageUrl] = useState("");
  const [portfolio, setPortfolio] = useState({
    title: "",
    description:"",
    image:"",
    like: 0
  });
  const handleClickOpen = () => {
    setShowModal(true);
  };
  const handleClose = ()=> {
    setShowModal(false);
  };
  const handleChange = (e)=> {
    setPortfolio({...portfolio, [e.target.name]: e.target.value});
  };
  const handleClickAdd= async ()=>{
    await axios.post("http://localhost:3031/portfolio", portfolio)
    .then(response)
  }
  // const handleClick= async () => {
  //    axios.post("http://localhost:3031/portfolio/", x)
  //    .then((response) => ShortTextTwoTone(response.data);
  //    setShowModal(false);
  //    window.location.reload();} );



  // };


  return (
    <>
      <Button
        color="lightGreen"
        type="button"
        onClick={(e) => setShowModal(true)}
        ripple="light"
      size="lg"
      >
      + Add new project to the portfolio 
      </Button>

      <Modal size="lg" active={showModal} toggler={() => setShowModal(false)}>
        <ModalHeader toggler={() => setShowModal(false)}>
          Add new project <br />
        </ModalHeader>
        <ModalBody>
          Title
          <Input
            type="text"
            color="lightBlue"
            size="lg"
            outline={false}
            size="lg"
          />
          Description
          <Textarea color="lightBlue" size="regular" outline={false} />



          {imageUrl ? (
            <a href={imageUrl} download>
              <img src={imageUrl} alt="upload" href={imageUrl} download />
            </a>) : null
        }<Input
        type="file"
        color="lightBlue"
        size="lg"
        outline={false}
      />


        </ModalBody>
        <ModalFooter>
          <Button
            color="red"
            buttonType="link"
            onClick={(e) => setShowModal(false)}
            ripple="dark"
          >
            Close
          </Button>

          <Button
            color="green"
            onClick={handleClick}
            ripple="light"
          >
            Save Changes
          </Button>
        </ModalFooter>
      </Modal>
    </>
  );
}
