import React from "react";
import Card from "@material-tailwind/react/Card";
import CardImage from "@material-tailwind/react/CardImage";
import CardBody from "@material-tailwind/react/CardBody";
import CardFooter from "@material-tailwind/react/CardFooter";
import H6 from "@material-tailwind/react/Heading6";
import Paragraph from "@material-tailwind/react/Paragraph";
import Button from "@material-tailwind/react/Button";
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import FavoriteIcon from '@material-ui/icons/Favorite';
import { useState } from "react";
import NewProject from "./NewProject";
import "./Projects.css";


export default function Projects() {
    const projects = [
        {title: "Jardin Botanique", description:"A park in Bordeaux where animals could do whatever they want, even to walk their master ", image:"", like: "8950"},
        {title: "Wahoot", description:"quizz game", image:"", like: "1054"},
        {title: "Mars Random Memories", description:"How to !bored on Mars", image:"", like: "1564"},
        {title: "Vélo de Fonction", description:"Go to work by bike, get your money back! ", image:"", like: "3980"}
    ]
    const [liked, setLiked] = useState(false);
    const handleClick = () => {
        //appel axios.patch
        setLiked(true)
    };
    return (
        <div >
            {projects.map((project) => {
                return (
                    <Card class="gallery">

                    <CardImage
                        src="/logo192.png"
                        alt="Card Image"
                        style={{height:"300px"}}
                    />

                    <CardBody>
                        <H6 color="gray">{project.title}</H6>
                        <Paragraph color="gray">
                            {project.description}
                        </Paragraph>
                    </CardBody>

                    <CardFooter>
                        <Button color="lightOrange" size="lg" ripple="light" onClick={handleClick}>
                            {!liked ? <FavoriteBorderIcon /> : <FavoriteIcon/>}
                           {project.like}
                        </Button>
                    </CardFooter>
                </Card>
                )
            })}

        <div>
            <NewProject />
        </div>
        </div>
    );
}
