import "./App.css";
import BarNav from "./main/BarNav";
import "@material-tailwind/react/tailwind.css";
import Projects from "./fonctionnalities/Projects";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Presentation from "./fonctionnalities/Presentation";
import ImageSlider from "./fonctionnalities/ImageSlider";
import { SliderData } from "./fonctionnalities/SliderData";
import { SettingsPowerRounded } from "@material-ui/icons";
import { useEffect, useState } from "react";
import axios from "axios";
// import Contact from "./fonctionnalities/Contact";

function App() {
  const [projects, setProjects] = useState([]);
  useEffect(() => {
    axios
      .get("http://localhost:3031/portfolio")
      .then((res) => res.data)
      .then((data) => {
        setProjects(data);
      });
  }, []);

  const doSearch = async (key) => {
   setProjects()
  };

  return (
    <div>
      <Router>
        <BarNav onSubmit={doSearch} />
        <Switch>
          <Route exact path="/" component={Presentation} />
          <Route
            exact
            path="/portfolio"
            render={(matchProps) => <Projects {...matchProps} />}
          />
          {/* <Route
            exact
            path="/contact"
            render={(matchProps) => <Contact {...matchProps} />}
          />
          */}
          <Route
            exact
            path="/sliderTest"
            render={(matchProps) => <ImageSlider slides={SliderData} />}
          />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
