import React, { useState } from "react";
import Card from "@material-tailwind/react/Card";
import CardImage from "@material-tailwind/react/CardImage";
import CardBody from "@material-tailwind/react/CardBody";
import CardFooter from "@material-tailwind/react/CardFooter";
import H6 from "@material-tailwind/react/Heading6";
import Paragraph from "@material-tailwind/react/Paragraph";
import Button from "@material-tailwind/react/Button";
import FavoriteBorderIcon from "@material-ui/icons/FavoriteBorder";
import FavoriteIcon from "@material-ui/icons/Favorite";
import NewProject from "./NewProject";
import "./Projects.css";
import ImageSlider from "./ImageSlider";
import { SliderData } from "./SliderData";
import { FaArrowAltCircleRight, FaArrowAltCircleLeft } from "react-icons/fa";
import { makeStyles } from "@material-ui/core";

const useStyle = makeStyles({
  slider: {
    position: "relative",
    height: "100vh",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  image: {
    width: "1000px",
    height: "600px",
    borderRadius: "10px",
  },
  leftArrow: {
    position: "absolute",
    top: "50%",
    left: "32px",
    fontSize: "3rem",
    color: "#000",
    zIndex: 10,
    cursor: "pointer",
    userSelect: "none",
  },
  rightArrow: {
    position: "absolute",
    top: "50%",
    right: "32px",
    fontSize: "3rem",
    color: "#000",
    zIndex: 10,
    cursor: "pointer",
    userSelect: "none",
  },
});
export default function Projects({ slides }) {
  const projects = [
    {
      title: "Jardin Botanique",
      description:
        "A park in Bordeaux where animals could do whatever they want, even to walk their master ",
      image: "",
      like: "8950",
    },
    { title: "Wahoot", description: "quizz game", image: "", like: "1054" },
    {
      title: "Mars Random Memories",
      description: "How to !bored on Mars",
      image: "",
      like: "1564",
    },
    {
      title: "Vélo de Fonction",
      description: "Go to work by bike, get your money back! ",
      image: "",
      like: "3980",
    },
  ];
  const classes = useStyle();
  const [current, setCurrent] = useState(0);
  const [liked, setLiked] = useState(false);

  const nextSlide = () => {
    setCurrent(current === slides.length - 1 ? 0 : current + 1);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? slides.length - 1 : current - 1);
  };

  if (!Array.isArray(slides) || slides.length <= 0) {
    return null;
  }

  const handleClick = () => {
    //appel axios.patch
    setLiked(true);
  };
  return (
    <div>
      {projects.map((project) => {
        return (
          <Card className="gallery">
            <CardImage src="" alt="Card Image" >
              <section className={classes.slider}>
                <FaArrowAltCircleLeft
                  className={classes.leftArrow}
                  onClick={prevSlide}
                />
                <FaArrowAltCircleRight
                  className={classes.rightArrow}
                  onClick={nextSlide}
                />
                {SliderData.map((slide, index) => {
                  return (
                    <div
                      className={index === current ? "slide active" : "slide"}
                      key={index}
                    >
                      {index === current && (
                          <img
                            src={slide.image}
                            alt=""
                            className={classes.image}
                          ></img>
                      )}
                    </div>
                  );
                })}
              </section>
              );
            </CardImage>

            <CardBody>
              <H6 color="gray">{project.title}</H6>
              <Paragraph color="gray">{project.description}</Paragraph>
            </CardBody>

            <CardFooter>
              <Button
                color="lightGreen"
                size="lg"
                ripple="light"
                onClick={handleClick}
              >
                {!liked ? <FavoriteBorderIcon /> : <FavoriteIcon />}
                {project.like}
              </Button>
            </CardFooter>
          </Card>
        );
      })}

      <div>
        <NewProject />
      </div>
    </div>
  );
}
